import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';


const Searchbar = () => {

    let history = useHistory();

    const [team, setTeam] = useState(null)
    const [fromDate, setFromDate] = useState(null)
    const [toDate, setToDate] = useState(null)


    // const addNewStudent = async () => {
    //     let formField = new FormData()
    //     formField.append('email',email)
    //     formField.append('address',address)
    //     formField.append('phone',phone)


    //     await axios({
    //       method: 'post',
    //       url:'http://localhost:8000/api/',
    //       data: formField
    //     }).then(response=>{
    //       console.log(response.data);
    //       history.push('/')
    //     })
    // }
   
    return (
        <div className="container">
            <div className="container">
      <div className="w-75 mx-auto shadow p-5">
        <h2 className="text-center mb-4">Find brewery of the game</h2>
    
        <div class="form-group col-md-2">
        <label for="inputTeam">Team name</label>
        <input name="team" id="inputTeam" value={team} onChange={(e) => setTeam(e.target.value)}/>
        </div>

        <div class="form-group col-md-2">
        <label for="inputFrom_date">From date</label>
        <input name="fromDate" id="inputFromDate" value={fromDate} onChange={(e) => setFromDate(e.target.value)}/>
        </div>

        <div class="form-group col-md-2">
        <label for="inputTo_date">To date</label>
        <input name="toDate" id="inputToDate" value={toDate} onChange={(e) => setToDate(e.target.value)}/>
        </div>

        <div class="form-group col-md-2" style="margin-top:32px">
            <input type="submit" value="Show results" class="btn btn-primary"/>
        </div>
            
           {/* <button className="btn btn-primary btn-block" onClick={addNewStudent}>Add Student</button>  */}
       
      </div>
    </div>
        </div>
    );
};

export default Searchbar;
